    private void factorialEnterActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_factorialEnterActionPerformed
        
        
        try{
          int num,num2;
            num=Integer.parseInt(factorialInput.getText());
            num2=num;
            while(num2!=1){
            num2=num2-1;
            num=num*num2;   
            factorialOutput.setText("the factorial of your number is "+num);
            
            }      
        }catch(NumberFormatException nfe){
            System.err.println("please entert a proper intager"+nfe);
        }
     
    }//GEN-LAST:event_factorialEnterActionPerformed

