    private void Enter2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_Enter2ActionPerformed
        try{
           int num,mod=0;
           boolean isprime=true;
           num=Integer.parseInt(PrimeInput.getText());
           for(int i=2;i<=num/2;i=i+1){
               mod=num%i;
           }
           if(mod==0){
               isprime=false;
           }
           if(isprime){
               PrimeOutput.setText("this is a prime number");
           }else{
               PrimeOutput.setText("this is not a prim number");
           }
        }catch(NumberFormatException nfe){
            System.err.println("there is a eror please try again"+nfe);
        }
        
    }//GEN-LAST:event_Enter2ActionPerformed

